import React from "react";

import "./form.scss";

function Form(handleApiCall) {

  const [method, setMethod] = useState(" ");
  const [url, setUrl] = useState(" ");

  function handleSubmit(e) {
    e.preventDefault();
    const formData = {
      method:   function methodHandler(e) {

        setMethod(e.target.value);
      },

      url:  function urlHandler(e) {

        setUrl(e.target.value);
      },
    };
    handleApiCall(formData);
  }

  function urlHandler(e) {

    setUrl(e.target.value);
  }

  function methodHandler(e) {

    setMethod(e.target.value);
  }


  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          <span>URL: {url} </span>
          <input name="url" type="text"  onChange={urlHandler} />
          <button type="submit">GO!</button>
        </label>

        <label className="methods" value={methodHandler}>
          <span value="get">GET</span>
          <span value="post">POST</span>
          <span value="put">PUT</span>
          <span value="delete">DELETE</span>
        </label>
      </form>
    </>
  );
}

export default Form;
